import itertools

def combinations(n, k):
    return int(factorial(n) / (factorial(k) * factorial(n - k)))

def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n - 1)

def main():
    print(combinations(17, 3) * combinations(15, 5))

if __name__ == "__main__":
    main()
